// --- 300 LINEAR ACHIEVEMENTS GENERATOR ---
const generateAchievements = (): Mission[] => {
    const achievements: Mission[] = [];
    const MAX_LEVEL = 50;

    const addSeries = (
        prefix: string,
        type: any,
        baseTarget: number, 
        descTemplate: (t: number) => string,
        rewardConfig: (level: number, target: number) => { type: string, amount: number }
    ) => {
        for (let level = 1; level <= MAX_LEVEL; level++) {
            const target = level * baseTarget;
            const reward = rewardConfig(level, target);
            achievements.push({
                id: `${prefix}_${target}`, 
                desc: descTemplate(target),
                type: type,
                category: 'ACHIEVEMENT',
                target: target,
                current: 0,
                reward: { type: reward.type as any, amount: reward.amount },
                completed: false,
                claimed: false
            });
        }
    };

    // Achievements Config
    addSeries('earn', 'EARN', 2000, (t) => `Ki·∫øm ${t.toLocaleString()} Xu`, (l, t) => ({ type: 'STAR', amount: Math.ceil(t / 200) }));
    addSeries('hv', 'HARVEST', 10, (t) => `Thu ho·∫°ch ${t} n√¥ng s·∫£n`, (l, t) => ({ type: 'STAR', amount: Math.ceil(t / 5) }));
    addSeries('water', 'WATER', 5, (t) => `T∆∞·ªõi c√¢y ${t} l·∫ßn`, (l, t) => ({ type: 'STAR', amount: Math.ceil(t / 2) }));
    addSeries('feed', 'FEED', 5, (t) => `Cho th√∫ ƒÉn ${t} l·∫ßn`, (l, t) => ({ type: 'STAR', amount: Math.ceil(t / 2) }));
    addSeries('quiz', 'QUIZ', 5, (t) => `Tr·∫£ l·ªùi ƒë√∫ng ${t} c√¢u ƒë·ªë`, (l, t) => ({ type: 'STAR', amount: t }));
    addSeries('fert', 'FERTILIZE', 2, (t) => `B√≥n ph√¢n ${t} l·∫ßn`, (l, t) => ({ type: 'STAR', amount: t * 2 }));

    return achievements;
};

export const FARM_ACHIEVEMENTS_DATA: Mission[] = generateAchievements();

